package tekrar;

public class Cast {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		long sonuc=124+545;
		System.out.println(sonuc);
		System.out.println("----------------------");
		
		int x,y;
		x=457;y=875;
		double sonuc1=x+y;
		System.out.println(sonuc1);
		System.out.println("----------------------");
		
		double d1=344.322;
		double d2=433.541;
		System.out.println(d1+d2);
		int sonuc3=(int)(d1+d2);
		System.out.println(sonuc3);
		System.out.println("----------------------");
		
		byte b1=25+65;
		System.out.println(b1);
		byte b2=21;
		byte b3=42;
		byte toplam=(byte)(b2+b3);
		System.out.println(toplam);
	}

}
